mapper.select:
SELECT username,password,is_superuser,is_active,is_staff
FROM auth_user
WHERE username ilike #{username}

SELECT agp.group_id, p.name FROM simplyscript_permission p inner join simplyscript_grouppermission agp on agp.permission_id = p.id WHERE agp.group_id in
#{group}
ORDER BY 1,2

mapper.update:
UPDATE auth_user
  SET password=#{password}
WHERE username ilike #{username}
